import { useState } from "react"

const App = () => {
  const [counter, setCounter] = useState(0)

  const incrementCounter = () => setCounter(counter + 1)

  const resetCounter = () => setCounter(0)

  return(
    <div>
      <div>{counter}</div>
      <button onClick={incrementCounter}>
        plus
      </button>
      <button onClick={resetCounter}>
        reset
      </button>
    </div>
  )
}
export default App
